#include <bits/stdc++.h>using namespace std; vector<int> z_function(const string &s) {    int n = s.size();    vector<int> z(n);    int l = 0, r = 0;    for (int i = 1; i < n; i++) {        if (i <= r)            z[i] = min(r - i + 1, z[i - l]);        while (i + z[i] < n && s[z[i]] == s[i + z[i]])            z[i]++;        if (i + z[i] - 1 > r)            l = i, r = i + z[i] - 1;    }    return z;} int main() {    string s;    cin >> s;    int n = s.size();    vector<int> z = z_function(s);     int maxZ = 0;    int res = 0;     for (int i = 1; i < n; i++) {        if (z[i] == n - i) {  // suffix that matches prefix            if (maxZ >= z[i]) {                res = z[i];                break;            }        }        maxZ = max(maxZ, z[i]);    }     if (res == 0)        cout << "Just a legend";    else        cout << s.substr(0, res);}