#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        long long a, b;        cin >> a >> b;         if (a == b) {            cout << 0 << "\n";            continue;        }        int H = 63 - __builtin_clzll(a);        long long limit = (1LL << (H + 1));         if (b >= limit) {            cout << -1 << "\n";            continue;        }         long long x = a ^ b;        if (x <= a) {            cout << 1 << "\n" << x << "\n";        } else {            long long full = (1LL << (H + 1)) - 1;            long long x1 = a ^ full;              long long x2 = full ^ b;              cout << 2 << "\n" << x1 << " " << x2 << "\n";        }    }    return 0;}  // #include "bits/stdc++.h"// #define int long long// #define uint unsigned long long// #define vi vector<int>// #define vvi vector<vi>// #define vb vector<bool>// #define vvb vector<vb>// #define fr(i, n) for (int i = 0; i < (n); i++)// #define rep(i, a, n) for (int i = (a); i <= (n); i++)// #define nl cout << "\n"// #define dbg(var) cout << #var << "=" << var << " "// #define all(v) v.begin(), v.end()// #define sz(v) (int)(v.size())// #define srt(v) sort(v.begin(), v.end())         // sort// #define mxe(v) *max_element(v.begin(), v.end()) // find max element in vector// #define mne(v) *min_element(v.begin(), v.end()) // find min element in vector// #define unq(v) v.resize(distance(v.begin(), unique(v.begin(), v.end())));// // make sure to sort before applying unique // else only consecutive duplicates would be removed// #define bin(x, y) bitset<y>(x)// using namespace std;// int MOD = 1e9 + 7; // Hardcoded, directly change from here for functions! // void modadd(int &a, int b) { a = ((a % MOD) + (b % MOD)) % MOD; }// void modsub(int &a, int b) { a = ((a % MOD) - (b % MOD) + MOD) % MOD; }// void modmul(int &a, int b) { a = ((a % MOD) * (b % MOD)) % MOD; }// // ================================== take ip/op like