#include <bits/stdc++.h>using namespace std;using ll = long long; int main(){    ios::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while(t--){        ll n;        cin >> n;        if(n == 2){            cout << -1 << "\n";            continue;        }        if(n == 5){            cout << "1 3\n";            cout << "1 4\n";            cout << "2 4\n";            cout << "2 5\n";            continue;        }        ll S0 = n*(n+1)/2 - 1;        ll k = ll(sqrt((long double)S0));        if(k*k < S0) k++;        if(k*k == S0){            for(ll i = 2; i <= n; ++i){                cout << 1 << " " << i << "\n";            }            continue;        }        bool found = false;        ll fi = -1, fj = -1;        for(ll pq = k; pq <= k+5 && !found; ++pq){            ll target = pq*pq;            ll diff = target - S0;            if(diff < 0) continue;            for(ll i = 2; i <= n; ++i){                if(diff % i == 0){                    ll j = diff / i + 1;                    if(j >= 2 && j <= n && j != i){                        fi = i;                        fj = j;                        found = true;                        break;                    }                }            }        }        for(ll i = 2; i <= n; ++i){            if(i == fi) continue;            cout << 1 << " " << i << "\n";        }        cout << fi << " " << fj << "\n";    }    return 0;}