#include <bits/stdc++.h>using namespace std;int T;int n,x,a,b,cnt1,cnt2;int p1[110],p2[110];int main(){	scanf("%d",&T);	for(;T--;)	{		scanf("%d%d%d",&x,&a,&b);		if(x==12) n=2;		else if(x==123) n=3;		else n=4;		for(int i=1;i<=n;i++) p1[i]=p2[i]=i;		for(int i=1;i<a;i++) next_permutation(p1+1,p1+n+1);		for(int i=1;i<b;i++) next_permutation(p2+1,p2+n+1);		cnt1=0;		for(int i=1;i<=n;i++) if(p1[i]==p2[i]) cnt1++;		printf("%dA%dB\n",cnt1,n-cnt1);	}	return 0;}