// https://codeforces.com/contest/2181/problem/A  #include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     long long n, m;    cin >> n >> m;     vector<string> s(n);    for (int i = 0; i < n; i++) cin >> s[i];     vector<vector<long long>> cnt(n, vector<long long>(26, 0));     for(int i = 0; i < n; i++) {        for (char ch : s[i]) {            cnt[i][ch - 'A']++;        }    }     vector<long long> tot(26, 0);    for (int i = 0; i < n; i++) {        for (int c = 0; c < 26; c++) {            tot[c] += cnt[i][c];        }    }     for (int l = 0; l < n; l++) {        long long ans = LLONG_MAX;        bool ok = true;         for (int c = 0; c < 26; c++) {            long long A = tot[c] - cnt[l][c];             if (A == 0) {                if (cnt[l][c] > 0) {                    ok = false;                    break;                }            }             // ek copy s[l] ki banao            else {                long long avl = m * A;                if (avl < cnt[l][c]) {                    ok = false;  // nahi ban sakti to false                    break;                }                // s[l] ban gayi to k copies baankiyon ki banao                long long k = (avl - cnt[l][c]) / A;                ans = min(ans, k);            }        }        if (!ok) cout << -1 << " ";        else cout << ans << " ";    }    cout << "\n";}